            // this.props.handleSubmit <- provide by redux form
            <form
                onSubmit={this.props.handleSubmit(this.onSubmit)}
                className='ui form'>
                <Field
                    name='title'
                    component={this.renderInput}
                    label='Title' />
                <Field
                    name='description'
                    component={this.renderInput}
                    label='Description' />
                <button className='ui button primary'>Submit</button>
            </form>
        )
    }
}

