import React from 'react'
import { Field, reduxForm } from 'redux-form'

class StreamCreate extends React.Component {
    renderInput({ input, label }) {
        return (
            <div className='field'>
                <label>{label}</label>
                <input {...input} />
            </div>
        )
    }

    onSubmit(e) {
        e.preventDefault();
    }

    render() {
        return (
            <form
                onSubmit={this.onSubmit}
                className='ui form'>
                <Field
                    name='title'
                    component={this.renderInput}
                    label='Title' />
                <Field
                    name='description'
                    component={this.renderInput}
                    label='Description' />
            </form>
        )
    }
}

export default reduxForm({
    form: 'streamCreate'
})(StreamCreate)
